       const clickNews = () => {
         if (isBalloon === true) { 
             dispatch(newsBalloonClicked())
         }
      }

    return (
        <div className="news-button-parent">
                <h1 onClick={clickNews} className="news-button">News</h1>

        <style jsx>{
                `
                .news-button-parent {
                    display: flex;
